import React, { Component } from "react";
import cateimg from "../images/cateimg.png";
import { FeatureData } from "../FeatureData";
export default class Featured extends Component {
  render() {
    return (
      <div style={{ marginTop: "111px" }}>
        <div class="m-auto text-center">
          <h2 style={{ fontSize: "41px" }}>Featured</h2>
        </div>
        <div class="container">
          <div className="feature-main">
            {FeatureData.map((Fdata) => {
              return (
                <div
                  style={{ display: "flex", marginTop: "72px" }}
                  class="col-sm-12 col-md-12 col-lg-6 mb-3"
                >
                  <div style={{ width: "315px", height: "355px" }}>
                    <img
                      className="feature-img"
                      src={cateimg}
                      alt="..."
                      style={{
                        borderRadius: "10px",
                        height: "56.8vh",
                        width: "100%",
                        borderBottomRightRadius: "0",
                      }}
                    />
                  </div>
                  <div style={{ width: "30%", alignSelf: "flex-end" }}>
                    <p
                      class="text-center"
                      style={{ marginBottom: "0px", fontSize: "22px" }}
                    >
                      Product Name
                    </p>
                    <p
                      class="text-center"
                      style={{ fontSize: "19px", paddingTop: "2px" }}
                    >
                      Category
                    </p>
                    <div
                      style={{
                        width: "200px",
                        backgroundColor: "#960200",
                        height: "55px",
                        borderRadius: "10px",
                        borderBottomLeftRadius: "0",
                        borderTopLeftRadius: "0",
                      }}
                    >
                      <p
                        style={{
                          textAlign: "center",
                          lineHeight: "60px",
                          color: "white",
                        }}
                      >
                        <span style={{ fontSize: "20px" }}>Price</span>
                        <span style={{ fontSize: "29px", paddingLeft: "42px" }}>
                          $120
                        </span>
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
}
