          <h2>Featured</h2>
        </div>
        <div class="container">
          <div style={{border:"5px solid red" , width:"45%" ,height:"30vh",display:"flex"}}>
            <div>
              <img
                src={got1}
                alt="..."
                style={{ borderRadius: "10px",width:'300px',height:"180px" }}
              />
            </div>
            <div style={{border:"5px solid green",width:'25%',paddingTop:'50px'}}>
                        <p class="text-center">Product Name</p>
                        <p class="text-center">Category</p>
                        <div class="text-center justify-center text-white" style={{width:"100%",backgroundColor:'red',height:'45px',borderRadius:'10px'}}><span>Price</span><span>$120</span></div>
            </div>
            {/* <div style={{ width: "375px", height: "250px",paddingRight:'50px', }}>
                        <img src={got1} class="card-img-top" alt="..." style={{borderRadius:'22.5px'}}/>
              <div class="card-body">
                <h5 class="card-title text-center">Card title</h5>
              </div>
            </div> */}
            {/* <div style={{ width: "375px", height: "250px",paddingRight:'50px', }}>
                        <img src={got1} class="card-img-top" alt="..." style={{borderRadius:'22.5px'}}/>
              <div class="card-body">
                <h5 class="card-title text-center">Card title</h5>
              </div>
            </div> */}
          </div>
        </div>
      </div>
    );
  }
}
